import React from 'react'

import './TaskPage.scss'

class DashboardPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      taskList : [],
      newTask : ""
    };
  }

  handleChange(event) {
    this.setState({newTask: event.target.value});
  }

  fetchTaskList(){
    fetch('/api/tasks/fetch')
      .then(response => response.json())
      .then(data => {
        this.setState({taskList : data})
      })
      .catch(err => console.log("ERREUR " + err));
  }

  addTask(){
    let tmp = this.state.taskList;
    tmp.push(this.state.newTask);
    this.setState({taskList : tmp});
    fetch('/api/tasks/add/'+this.state.newTask)
      .then(response => response.json())
      .then(data => {
        console.log(data)
        this.fetchTaskList()
        alert("Successfuly added")
      })
      .catch(err => console.log("ERREUR " + err));
  }

  deleteTask(event){
    fetch('/api/tasks/delete/'+event.target.id)
      .then(response => response.json())
      .then(data => {
        console.log(data)
        this.fetchTaskList()
        alert("Successfuly deleted")
      })
      .catch(err => console.log("ERREUR " + err));
  }

  componentDidMount(){
    this.fetchTaskList()
  }

  render(){
    return (
      <>
        <h1 id="titreApp">TodoApp</h1>
        <div id="list" className="list">
          {this.state.taskList.map((item, i) => {
            console.log(this.state.taskList)
            return (<div key={i}><p>{item}</p><button id={i} onClick={this.deleteTask.bind(this)}>delete</button></div>) 
          })}
        </div>
        <input type="text" onChange={this.handleChange.bind(this)}></input>
        <button onClick={this.addTask.bind(this)}>Add a task</button>
      </>
    )
  }
}

export default DashboardPage;
