import React, { useState, useEffect } from 'react'
import { Route, Link, BrowserRouter as Router } from 'react-router-dom'

// Ajout à faire
// Rendre plus joli avec Bootstrap
// NoSql -> créer API pour ça
// Ajout de contact
// Page de rappel d'event peut être cool aussi
// Attendre le logo pour l'ajouter
let taskList = [];

class DashboardPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {date: new Date()};
  }

  fetchTaskList(){
    return ['hello', 'hello2'];
  }

  componentDidMount(){
    console.log(taskList)
    taskList = this.fetchTaskList();
    console.log(taskList)
  }

  render(){
    return (
      <>
        <h1 id="titreApp">TodoApp</h1>
        <div id="list">
          {taskList.map((item, i) => {
            return <p id={i}>{item}</p>
          })}
        </div>
      </>
    )
  }
}

export default DashboardPage;

