import React from "react";
import Pi from "../../../Pi/Pi";
import {Button} from "react-bootstrap";

function Hours({
  pis,
  pi,
  setExistingItemText,
  updateItem
}) {
  return (
    <div>
      {pis.map((item) => {
        const { _id } = item;
        console.log(item)
        console.log(_id)
        var onSaveEvent = (e) => updateItem(_id);
        return (
          <>
          <Pi key={_id} id={_id} nom={item.name} prenom={item.surname} timing={item.hours} totem={item.totem} admin={true}/>
          <Button onClick={onSaveEvent}>Save</Button>
          </>
        );
      })}
    </div>
  );
}

export default Hours;
