import React from "react";
import Hours from './'

class HoursContainer extends React.Component {
    constructor() {
        super();
        this.state = {
            pis: [],
            pi: {
                prenom: "",
                nom: "",
                totem: "",
                timing: "",
            }
        };

        this.setExistingItemText = this.setExistingItemText.bind(this);
        this.updateItem = this.updateItem.bind(this);
    }

    render() {
        console.log("render:", this.state.items);
        return (
            <Hours
                items={this.state.items}
                formItem={this.state.formItem}
                setExistingItemText={this.setExistingItemText}
                updateItem={this.updateItem}
            />
        );
    }

    async componentDidMount() {
        this.loadItems();
    }

    loadItems() {
        fetch('/users/listeUser')
            .then(response => response.json())
            .then(data => {
                if (data.error)
                    return alert("Error:" + data.error);

                this.setState({ pis: data });
            })
            .catch(err => console.error("[HoursContainer] Error when fetching users:", err));
    }

    setExistingItemText(newValue) {
        console.log("GalleryContainer::setExistingItemText", newValue);

        const {formItem} = this.state;

        const newFormItem = {
            ...formItem,
            timing: newValue
        };

        this.setState({
            formItem: newFormItem
        });
    }


}

export default HoursContainer;
