import React from 'react'

class DashboardPage extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.state = {
      taskList : [],
      newTask : ""
    };
  }

  handleChange(event) {
    this.setState({newTask: event.target.value});
  }

  fetchTaskList(){
    fetch('/api/tasks/fetch')
      .then(response => response.json())
      .then(data => {
        this.setState({taskList : data})
      })
      .catch(err => console.log("ERREUR " + err));
  }

  addTask(){
    let tmp = this.state.taskList;
    tmp.push(this.state.newTask);
    this.setState({taskList : tmp});
  }

  componentDidMount(){
    this.fetchTaskList()
  }

  render(){
    return (
      <>
        <h1 id="titreApp">TodoApp</h1>
        <div id="list">
          {this.state.taskList.map((item, i) => {
            console.log(this.state.taskList)
            return <p id={i} key={i}>{item}</p>
          })}
        </div>
        <input type="text" onChange={()=>this.handleChange()}></input>
        <button onClick={()=>this.addTask()}>Add a task</button>
      </>
    )
  }
}

export default DashboardPage;
